function InitAggCalculator(){jQuery("#ac_calculate").click(CalculateMaterial).trigger("click"),jQuery(".aggregateCalculator select, .aggregateCalculator input[type=text]").change(CalculateMaterial)}function CalculateMaterial(){var e=ToMillimeters(jQuery("#ac_length").val(),jQuery("#ac_length_units option:selected").val()),t=ToMillimeters(jQuery("#ac_width").val(),jQuery("#ac_width_units option:selected").val()),a=ToMillimeters(jQuery("#ac_depth").val(),jQuery("#ac_depth_units option:selected").val());aggregate=jQuery("#ac_aggregate option:selected").val(),mm3=e*t*a,agg_mm3=mm3*aggregate,tonnage=(agg_mm3/1e9).toFixed(1),jQuery("#agg_required").text(tonnage),jQuery("#agg_required_bulk").text((tonnage/.75).toFixed(2)),jQuery("#agg_required_bags").text((tonnage/.02).toFixed(2))}function ToMillimeters(e,t){switch(multiplier=1,parseInt(t)){case 1:multiplier=1;break;case 2:multiplier=1e3;break;case 3:multiplier=25.4;break;case 4:multiplier=304.8;break;case 5:multiplier=914.4;break;default:multiplier=1}return e*multiplier}jQuery(InitAggCalculator);